mapper.select:
/* UserMapper.selectAlarm */
select count(*)
 from alarm
where farm_id = #{user_id}
  and alarm_read_yn = 'N'

mapper.update:
/* UserMapper.selectAlarm */
update alarm
   set alarm_read_yn = 'Y'
     , mod_id = #{user.user_id}
     , mod_date = CURRENT_TIMESTAMP
 where farm_id = #{user.user_id}
   and alarm_id = #{alarmReadReqDto.alarmId}
