mapper.select:
/* FarmMapper.selectFarms */ select a.farm_id as farmId , case when a.farm_kind = '01' THEN '농장' when a.farm_kind = '02' THEN '목장' else '체험' end as farmKindNm , a.farm_name as farmName , a.farm_zip as farmZip , concat(format(a.farm_use_amt, 0), '원') as farmUseAmt , c.file_url as farmMainImageUrl , '0' as reviewStar , '0명' as reviewStarCnt from farm a left join file_group b on a.farm_main_image = b.file_group_id left join file c on b.file_group_id = c.file_group_id where farm_status = '01' and a.farm_kind = #{farmKind}
and a.farm_name like concat('%', #{farmName}, '%')
and a.farm_use_day like concat('%', #{farmUseDay}, '%')
and a.farm_max_user_cnt >= CAST(#{farmMaxUserCnt} AS UNSIGNED)
order by a.created_date desc

/* FarmMapper.selectFarm */
select count(*)
  from farm
 where farm_name = #{farmName}

mapper.update:
/* FarmMapper.insertFarm */
insert into farm (
            farm_id
          , farm_app_id
          , farm_app_pw
          , farm_area
          , farm_name
          , farm_owner_name
          , farm_fond_de
          , farm_intrcn
          , farm_zip
          , farm_owner_tel
          , farm_page
          , farm_zip_detail
          , farm_zip_code
          , farm_latitude
          , farm_longitude
          , farm_status
          , created_id
          , created_date
          , mod_id
          , mod_date
  ) values (
            UUID()
          , #{id}
          , #{pw}
          , #{row.AREA}
          , #{row.FARM_NM}
          , #{row.RPRSNTV}
          , #{row.FOND_DE}
          , #{row.FARM_INTRCN}
          , #{row.ADDR}
          , #{row.TLPHON_NO}
          , #{row.HMPG}
          , #{row.RDNMADR}
          , #{row.NW_ZIP}
          , #{row.LA}
          , #{row.LO}
          , '00'
          , 'system'
          , CURRENT_TIMESTAMP
          , 'system'
          , CURRENT_TIMESTAMP
          )

mapper.select:
/* FarmMapper.selectFarmByFarmIdForFarm */
select *
  from farm
 where farm_id = #{farmId}

/* FarmMapper.selectFarmByFarmAppIdAndFarmAppPw */
select *
  from farm
 where farm_app_id = #{farmAppId}
   and farm_app_pw = #{farmAppPw}
