mapper.insert:
/* ReservationMapper.insertReservation */
insert into reservation (
            reservation_id
          , user_id
          , farm_id
          , reservation_name
          , reservation_email
          , reservation_tel
          , reservation_date
          , reservation_participants
          , reservation_status
          , reservation_start_time
          , reservation_end_time
          , created_id
          , created_date
          , mod_id
          , mod_date
 ) values (
            #{reservationId}
          , #{user.user_id}
          , #{reservationReqDto.farmId}
          , #{reservationReqDto.reservationName}
          , #{reservationReqDto.reservationEmail}
          , #{reservationReqDto.reservationTel}
          , #{reservationReqDto.reservationDate}
          , #{reservationReqDto.reservationParticipants}
          , '00'
          , #{reservationReqDto.reservationStartTime}
          , #{reservationEndTime}
          ,'system'
          , CURRENT_TIMESTAMP
          , 'system'
          , CURRENT_TIMESTAMP
         )

mapper.update:
/* ReservationMapper.reservationFarmCancel */
update reservation
   set reservation_status = '02'
     , mod_id = #{user.user_id}
     , mod_date = CURRENT_TIMESTAMP
 where reservation_id = #{reservationCancelReqDto.reservationId}

mapper.select:
/* ReservationMapper.reservationFarmCancel */
select *
  from reservation
 where reservation_id = #{reservationId}

/* ReservationMapper.duplicationReservationCnt */
select COUNT(*)
  from reservation
 where user_id = #{user.user_id}
   and farm_id = #{reservationReqDto.farmId}
   and reservation_date = #{reservationReqDto.reservationDate}
   and STR_TO_DATE(#{reservationReqDto.reservationStartTime}, '%H:%i') < STR_TO_DATE(reservation_end_time, '%H:%i')
   and STR_TO_DATE(#{reservationEndTime}, '%H:%i') > STR_TO_DATE(reservation_start_time, '%H:%i')

/* ReservationMapper.duplicationReservationCnt */
select count(*)
  from farm
 where farm_id = #{farmId}
   and farm_use_day like concat('%',#{day},'%')
